function exec(){
	window.onerror=function(){}
	if(typeof jQuery != "undefined" && typeof AnyPic != "undefined"){
		clearInterval(intl);
	}else{
		console.log("Waiting jQuery and AnyPic loaded!");
		return;
	}
	jQuery.noConflict();
	var anyPic=new AnyPic();
	anyPic.fetchZoomPic=function(img){
		if(typeof img != "undefined" && img){
			return img.src.replace(/sinaimg\.cn\/.+?\//ig,"sinaimg.cn/large/");
		}
	}
	jQuery(function(){
		var imgPics=new Array();
		var picIntel;
		function exePic(callback){
			imgs=jQuery("ul#slider img");
			if(imgs.length>0&&imgs.filter("[loaded]").length<imgs.length){
				clearInterval(picIntel);
			}else{
				return;
			}
			imgs.parent("a").click(function(event){
				event.preventDefault();
			});
			var idx;
			imgs.each(function(index){
				if(!jQuery(this).attr("loaded")){
					idx=index;
					return false;
				}
			});
			imgs.attr("loaded","true");
			imgPics=imgs;
			anyPic.initImgList(imgPics);
			console.log("Get pic: ",imgs.length);
			if(typeof callback =="function"){
				callback(idx);
			}
		}
		picIntel=setInterval(exePic,300);

		anyPic.fetchPrevPicId=function(currentIndex,imgIdList){
			if(currentIndex==0&&!jQuery("#prev").hasClass('btn_dis')){
				jQuery("#prev").get(0).click();
				anyPic.hide();
			}else{
				return currentIndex==0?imgIdList[imgIdList.length-1]:imgIdList[currentIndex-1];
			}
		}

		anyPic.fetchNextPicId=function(currentIndex,imgIdList){
			if(currentIndex==imgIdList.length-1&&!jQuery("#next").hasClass('btn_dis')){
				jQuery("#next").get(0).click();
				anyPic.hide();
			}else{
				return currentIndex==imgIdList.length-1?imgIdList[0]:imgIdList[currentIndex+1];
			}
		}

		jQuery("#prev,#next").click(function(){
			var btn=this;
			picIntel=setInterval(function(){
				exePic(function(idx){
					if(idx){
						imgPics[idx].click();
						return;
					}
					if(btn.id=="next"){
						imgPics[0].click();
					}else{
						imgPics[imgPics.length-1].click();
					}
					
				});
			},1000);
		});
	});
}
var intl=setInterval(exec,500);
